import React, { useState, useEffect } from 'react';
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  Image,
  TouchableOpacity,
  TextInput,
} from 'react-native';
import { getFirestore, collection, query, onSnapshot, addDoc } from 'firebase/firestore';
import { auth } from '../config/firebase';

import { createStackNavigator } from '@react-navigation/stack';

const Stack = createStackNavigator();

export default function App() {
  return (
	<Stack.Navigator>
	  <Stack.Screen name="Joueurs" component={Joueurs} />
	  <Stack.Screen name="Details" component={Details} />
	</Stack.Navigator>
  );
}

function Joueurs({ navigation }) {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchText, setSearchText] = useState('');

  useEffect(() => {
	const q = query(collection(getFirestore(), 'users'));
	const unsubscribe = onSnapshot(q, (snapshot) => {
	  const users = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
	  setUsers(users);
	  filterAndSortUsers(searchText, users);
	});

	return () => {
	  unsubscribe();
	};
  }, []);

  const filterAndSortUsers = (text, players) => {
	const filtered = players.filter((player) =>
	  player.name.toLowerCase().includes(text.toLowerCase())
	);
	filtered.sort((a, b) => b.butPlayer - a.butPlayer);
	setFilteredUsers(filtered);
  };

  useEffect(() => {
	filterAndSortUsers(searchText, users);
  }, [searchText, users]);

  const renderItem = ({ item }) => (
	<TouchableOpacity
	  style={styles.item}
	  onPress={() => navigation.navigate('Details', { user: item })}
	>
	  <Image style={styles.itemImage} source={{ uri: item.imageUrl }} />
	  <View style={styles.itemContent}>
		<Text style={styles.itemTitle}>{item.name}</Text>
		<Text style={styles.itemSubtitle}>{item.positionPlayer}</Text>
		<Text style={styles.itemSubtitle}>Buts:{item.butPlayer}</Text>
		<Text style={styles.itemSubtitle}>Assist:{item.assistPlayer}</Text>
	  </View>
	</TouchableOpacity>
  );

  return (
	<View style={styles.container}>
	  <TextInput
		style={styles.searchBar}
		value={searchText}
		onChangeText={setSearchText}
		placeholder="Rechercher"
	  />
	  <FlatList
		data={filteredUsers}
		renderItem={renderItem}
		keyExtractor={(item) => item.id}
	  />
	</View>
  );
}

function Details({ route, navigation }) {
  const { user } = route.params;

  return (
	<View style={styles.selectedUserContainer}>
	  <TouchableOpacity onPress={() => navigation.goBack()}>
	  </TouchableOpacity>
	  <Image style={styles.selectedUserImage} source={{ uri: user.imageUrl }} />
	  <View style={styles.selectedUserContent}>
		<Text style={styles.selectedUserTitle}>{user.name}</Text>
		<Text style={styles.selectedUserSubtitle}>{user.positionPlayer}</Text>
		<Text style={styles.selectedUserSubtitle}>Buts:{user.butPlayer}</Text>
		<Text style={styles.selectedUserSubtitle}>Assist:{user.assistPlayer}</Text>
	  </View>
	</View>
  );
}

// Ajoutez ce style
const styles = StyleSheet.create({
  goBack: {
	fontSize: 18,
	textAlign: 'left',
	padding: 10,
	color: 'blue',
  },
container: {
flex: 1,
backgroundColor: '#F6F7FB',
padding: 10,
},
flatListStyle: {
position: 'absolute',
bottom: 0,
width: '100%',
height: '60%',
backgroundColor: '#F6F7FB',
borderTopWidth: 1,
marginRight: 10,
marginLeft: 10,
borderTopColor: '#ccc',
},
item: {
flexDirection: 'row',
alignItems: 'center',
paddingVertical: 10,
borderBottomWidth: 1,
borderBottomColor: '#ccc',
},
itemImage: {
width: 50,
height: 50,
borderRadius: 25,
marginRight: 10,
},
itemContent: {
flex: 1,
},
itemTitle: {
fontSize: 18,
fontWeight: 'bold',
},
itemSubtitle: {
fontSize: 16,
},
selectedUserContainer: {
backgroundColor: '#FFFFFF',
flexDirection: 'row',
alignItems: 'center',
paddingVertical: 10,
borderBottomWidth: 1,
borderBottomColor: '#ccc',
marginBottom: 10,
height: 300,
},
selectedUserImage: {
width: 80,
height: 80,
borderRadius: 40,
marginRight: 10,
},
selectedUserContent: {
flex: 1,
},
selectedUserTitle: {
fontSize: 22,
fontWeight: 'bold',
},
selectedUserSubtitle: {
fontSize: 20,
},
searchBar: {
	height: 40,
	borderColor: 'gray',
	borderWidth: 1,
	paddingLeft: 10,
	marginBottom: 10,
	borderRadius: 5,
  },
});
