import React from 'react';
import {Animated, Dimensions, StyleSheet, Text, View, Image, TouchableOpacity} from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { NavigationContainer } from '@react-navigation/native';
import { FontAwesome5 } from '@expo/vector-icons';
import { useRef, useState } from 'react';

import HomeScreen from '../screens/HomeScreen';
import FindScreen from '../screens/FindScreen';
import ChatScreen from '../screens/ChatScreen';
import PostScreen from '../screens/PostScreen';
import SettingScreen from '../screens/SettingScreen';

import "react-native-gesture-handler";
import {
  Button,
  Pressable,
  Switch,
  useWindowDimensions,
} from "react-native";
import {
  BottomSheetModal,
  BottomSheetModalProvider,
} from "@gorhom/bottom-sheet";
import { AntDesign } from "@expo/vector-icons";
import { Entypo } from "@expo/vector-icons";

import plus from '../assets/plus.png'

const Tab = createBottomTabNavigator();

const Tabs = () => {

 const [darkmode, setDarkmode] = useState(false);
  const [device, setDevice] = useState(false);
  const { width } = useWindowDimensions();
  const [theme, setTheme] = useState("dim");
  const [isOpen, setIsOpen] = useState(false);

  const bottomSheetModalRef = useRef(null);

const snapPoints = ["25%", "48%", "75%"];
function handlePresentModal() {
	bottomSheetModalRef.current?.present();
	setTimeout(() => {
	  setIsOpen(true);
	}, 100);
  }

const tabOffsetValue = useRef(new Animated.Value(0)).current;	

	return(
		<NavigationContainer independent={true}>
		<BottomSheetModalProvider>
		  <View
			style={[
			  styles.container,
			  { backgroundColor: isOpen ? "gray" : "white" },
			]}
		  >
			<Button title="Present Modal" onPress={handlePresentModal} />
			<StatusBar style="auto" />
			<BottomSheetModal
			  ref={bottomSheetModalRef}
			  index={1}
			  snapPoints={snapPoints}
			  backgroundStyle={{ borderRadius: 50 }}
			  onDismiss={() => setIsOpen(false)}
			>
			  <View style={styles.contentContainer}>
				<Text style={[styles.title, { marginBottom: 20 }]}>Dark mode</Text>
				<View style={styles.row}>
				  <Text style={styles.subtitle}>Dark mode</Text>
				  <Switch
					value={darkmode}
					onChange={() => setDarkmode(!darkmode)}
				  />
				</View>
				<View style={styles.row}>
				  <Text style={styles.subtitle}>Use device settings</Text>
				  <Switch value={device} onChange={() => setDevice(!device)} />
				</View>
				<Text style={styles.description}>
				  Set Dark mode to use the Light or Dark selection located in your
				  device Display and Brightness settings.
				</Text>
				<View
				  style={{
					width: width,
					borderBottomWidth: StyleSheet.hairlineWidth,
					borderBottomColor: "gray",
					marginVertical: 30,
				  }}
				/>
				<Text style={[styles.title, { width: "100%" }]}>Theme</Text>
				<Pressable style={styles.row} onPress={() => setTheme("dim")}>
				  <Text style={styles.subtitle}>Dim</Text>
				  {theme === "dim" ? (
					<AntDesign name="checkcircle" size={24} color="#4A98E9" />
				  ) : (
					<Entypo name="circle" size={24} color="#56636F" />
				  )}
				</Pressable>
				<Pressable style={styles.row} onPress={() => setTheme("lightsOut")}>
				  <Text style={styles.subtitle}>Lights out</Text>
				  {theme === "lightsOut" ? (
					<AntDesign name="checkcircle" size={24} color="#4A98E9" />
				  ) : (
					<Entypo name="circle" size={24} color="#56636F" />
				  )}
				</Pressable>
			  </View>
			</BottomSheetModal>
		  </View>
		</BottomSheetModalProvider>	
		<Tab.Navigator 
		  screenOptions={{
			headerShown: false,
			tabBarShowLabel:false,
			tabBarStyle: {
				  position: 'absolute',
				  bottom: 40,
				  marginHorizontal: 20,
				  backgroundColor: '#ffffff',
				  borderRadius: 10,
				  height: 60,
				  ...styles.shadow
			  }

		  }}>
			<Tab.Screen name="Home" component={HomeScreen} options={{
				tabBarIcon: ({ focused }) => (
					<View style={{
						position: 'absolute',
						top: '50%'
					}}>
					<FontAwesome5
					name="home"
					size={20}
					color={focused ? '#d4163a' : '#0f3b7a'}
					></FontAwesome5>
					</View>
				)
			}}listeners={({ navigation, route }) => ({
			  // Onpress Update....
			  tabPress: e => {
				Animated.spring(tabOffsetValue, {
				  toValue: 0,
				  useNativeDriver: true
				}).start();
			  }
			})}></Tab.Screen>
			<Tab.Screen name="search" component={FindScreen} options={{
				tabBarIcon: ({ focused }) => (
					<View style={{
						position: 'absolute',
						top: '50%'
					}}>
					<FontAwesome5
					name="search"
					size={20}
					color={focused ? '#d4163a' : '#0f3b7a'}
					></FontAwesome5>
					</View>
				)
			}}listeners={({ navigation, route }) => ({
			  // Onpress Update....
			  tabPress: e => {
				Animated.spring(tabOffsetValue, {
				  toValue: getWidth(),
				  useNativeDriver: true
				}).start();
			  }
			})}></Tab.Screen>
				<Tab.Screen name={"Post"} component={PostScreen} options={{
				  tabBarIcon: ({ focused }) => (

					<TouchableOpacity>
					  <View style={{
						width: 55,
						height: 55,
						backgroundColor: '#d4163a',
						borderRadius: 30,
						justifyContent: 'center',
						alignItems: 'center',
						marginBottom: Platform.OS == "android" ? 50 : 30
					  }}>
						<Image source={plus} style={{
						  width: 22,
						  height: 22,
						  tintColor: 'white',
						}}></Image>
					  </View>
					</TouchableOpacity>
				  )
				}}></Tab.Screen>
			<Tab.Screen name="Setting" component={SettingScreen} options={{
				tabBarIcon: ({ focused }) => (
					<View style={{
						position: 'absolute',
						top: '50%'
					}}>
					<FontAwesome5
					name="cog"
					size={20}
					color={focused ? '#d4163a' : '#0f3b7a'}
					></FontAwesome5>
					</View>
				)
			}}listeners={({ navigation, route }) => ({
			  // Onpress Update....
			  tabPress: e => {
				Animated.spring(tabOffsetValue, {
				  toValue: getWidth() * 3,
				  useNativeDriver: true
				}).start();
			  }
			})}></Tab.Screen>
			<Tab.Screen name="Chat" component={ChatScreen} options={{
				tabBarIcon: ({ focused }) => (
					<View style={{
						position: 'absolute',
						top: '50%'
					}}>
					<FontAwesome5
					name="user-alt"
					size={20}
					color={focused ? '#d4163a' : '#0f3b7a'}
					></FontAwesome5>
					</View>
				)
			}}listeners={({ navigation, route }) => ({
			  // Onpress Update....
			  tabPress: e => {
				Animated.spring(tabOffsetValue, {
				  toValue: getWidth() * 4,
				  useNativeDriver: true
				}).start();
			  }
			})}></Tab.Screen>
			</Tab.Navigator>
			<Animated.View style={{
				width: getWidth() - 10,
				height: 3,
				backgroundColor: '#d4163a',
				position: 'absolute',
				bottom: 98,
				// Horizontal Padding = 20...
				left: 45,
				borderRadius: 20,
				transform: [
				  { translateX: tabOffsetValue }
				]
			  }}>

			  </Animated.View>
		</NavigationContainer>
	);
}

function getWidth() {
  let width = Dimensions.get("window").width

  // Horizontal Padding = 20...
  width = width - 80

  // Total five Tabs...
  return width / 5
}


const styles = StyleSheet.create({
	shadow: {
		shadowColor: '#000',
		shadowOffset: {
			width: 10,
			height: 10,
		},
		shadowOpacity: 0.06,
		shadowRadius: 3.5,
		elevation: 5
	}
});

export default Tabs;
