import { View, TouchableOpacity, Text, Image, StyleSheet } from "react-native";
import { useNavigation, NavigationContainer } from "@react-navigation/native";
import { FontAwesome } from '@expo/vector-icons';
import colors from '../colors';
import { Entypo } from '@expo/vector-icons';
import Tabs from '../navigation/tabs';

import { auth } from '../config/firebase'

const Home = () => {

    const navigation = useNavigation();

    useEffect(() => {
        navigation.setOptions({
            headerLeft: () => (
                <FontAwesome name="home" size={24} color={colors.gray} style={{marginLeft: 15}} onPress={handleSignOut}/>
            ),
            headerRight: () => (
                <FontAwesome name="user" size={24} color={colors.gray} style={{marginRight: 15}}/>
            ),
        });
    }, [navigation]);
    const handleSignOut = () => {
        auth
          .signOut()
          .then(() => {
            navigation.replace("Login")
          })
          .catch(error => alert(error.message))
      }


    return (
        <View style={styles.container}>
           <Text>Nom du joueur: {auth.currentUser?.email}</Text>
           <Text>Email: {auth.currentUser?.email}</Text>
           <Text>Utilisateur ID: {auth.currentUser?.uid}</Text>
           <TouchableOpacity
               onPress={handleSignOut}
               style={styles.button}
             >
               <Text style={styles.buttonText}>DÃ©connexion</Text>
             </TouchableOpacity>
             <NavigationContainer independent={true}>
             <Tabs />
             </NavigationContainer>
        </View>
    );
    };

    export default Home;

    const styles = StyleSheet.create({
        container: {
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: "#F6F7FB",
        },
        button: {
            backgroundColor: '#d4163a',
            width: '60%',
            padding: 15,
            borderRadius: 10,
            alignItems: 'center',
            marginTop: 40,
          },
